  //state를 firebase.authService.currentUser에서 읽어와 알 수 있다 true or false
  return (
    <>
      <AppRouter isLoggedIn={isLoggedIn} />
      <footer>&copy; Nwitter {new Date().getFullYear()}</footer>
    </>
  );
}

export default App;
